#include "../inc/DAC5.h"
#include "../inc/Timer.h"

uint32_t counter;
uint32_t arr_size;
uint8_t* curr_sound_ptr;


void SysTick_IntArm(uint32_t period, uint32_t priority){
  SysTick->CTRL  = 0x00;      // disable during initialization
  SysTick->LOAD  = period-1;  // set reload register
  //The ARM Cortex-M0+ only implements the most significant 2 bits of each 8-bit priority field (giving the 4 priority levels).
  SCB->SHP[1]    = (SCB->SHP[1]&(~0xC0000000))|priority<<30;    // set priority (bits 31,30)
  SysTick->VAL   = 0;         // clear count, cause reload
  SysTick->CTRL  = 0x07;      // Enable SysTick IRQ and SysTick Timer
}
// initialize a 11kHz SysTick, however no sound should be started
// initialize any global variables
// Initialize the 5 bit DAC
void Sound_Init(void){
    uint32_t prio = 0; // highest priority
    counter = 0; arr_size = 0; // initializes global vars so that SysTick doesn't cause DAC to start w/ random sound
    SysTick_IntArm(7272, 0); // period given by (1/11000) / (1/80000000) = 7272.72.....;
    DAC5_Init(); // initialize our 5-bit DAC for output
}


void SysTick_Handler(void){ // called at 11 kHz
  // output one value to DAC if a sound is active
    // output one value to DAC if a sound is active
    if(counter != arr_size) {
        DAC5_Out(*(curr_sound_ptr + counter));
        counter++;
    } else {
        SysTick->LOAD = 0; // stop playing sound, stop SysTick interrupts
    }
}

//******* Sound_Start ************
// This function does not output to the DAC. 
// Rather, it sets a pointer and counter, and then enables the SysTick interrupt.
// It starts the sound, and the SysTick ISR does the output
// feel free to change the parameters
// Sound should play once and stop
// Input: pt is a pointer to an array of DAC outputs
//        count is the length of the array
// Output: none
// special cases: as you wish to implement
void Sound_Start(const uint8_t *pt, uint32_t count){
// write this
    curr_sound_ptr = pt;
    arr_size = count;
    counter = 0;
    SysTick->LOAD = 7272;   // load up the SysTick period
    SysTick->VAL = 0;       // writing to it causes clear & reload
}


